mapper.update:
UPDATE orders
number = #{number}, status = #{status}, user_id = #{userId}, address_book_id = #{addressBookId}, order_time = #{orderTime}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, amount = #{amount}, remark = #{remark}, phone = #{phone}, address = #{address}, user_name = #{userName}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, pack_amount = #{packAmount}, tableware_number = #{tablewareNumber}, tableware_status = #{tablewareStatus}
WHERE id = #{id}

mapper.select:
SELECT *
FROM orders
WHERE 1=1


    and number like concat('%',#{number},'%')


    and phone like concat('%',#{phone},'%')


    AND status = #{status}


    AND order_time >= #{beginTime}


    AND order_time <= #{endTime}


    AND user_id = #{userId}

order by orders.order_time desc

select sum(amount)
from orders

    status = #{status}

        and order_time >= #{begin}

    and order_time <= #{end}

select count(*)
from orders

    1=1
    and status = #{status}
    and order_time >= #{start}
    and order_time <= #{over}

select od.name name, sum(od.number) number
from orders o,order_detail od

    o.id = od.order_id
    and o.status = #{status}
    and o.order_time >= #{start}
    and o.order_time <= #{over}

group by od.name
order by number desc
limit 0,10
