mapper.select:
select s.*,c.name categoryName
from setmeal s
left outer join category c on s.category_id = c.id

    s.category_id = #{categoryId}
    and s.name like concat('%', #{name}, '%')
    and s.status = #{status}

select s.*,c.name categoryName
from setmeal s left outer join category c on s.category_id = c.id
where s.id = #{id}
